<template>
	<section class="scrolling-component" ref="scrollcomponent" name="lang">
		<section class="container my-body">
			<h1>
				{{ translate("formNewInterventionTitle") }}
			</h1>
			<hr />
			<form class="form-signin" v-on:submit.prevent="send">
				<section class="row">
					<section class="col-md-6 g-4">
						<label for="bdate" class="form-label">{{
							translate("formNewInterventionDate")
						}}</label>
						<v-date-picker
							:locale="lang"
							is-expanded
							v-model="range"
							is-range
							is24hr
							mode="datetime"
						/>
					</section>
					<section class="col-md-6 g-4">
						<label for="observations" class="form-label">{{
							translate("formNewInterventionDescription")
						}}</label>
						<textarea
							rows="5"
							v-model="form.description"
							class="form-control"
							id="username"
							style="font-size: small"
						/>
						<label for="observations" class="form-label">{{
							translate("formNewApiaryObservations")
						}}</label>
						<textarea
							rows="5"
							v-model="form.observations"
							class="form-control"
							id="username"
							style="font-size: small"
						/>
						<label for="type" class="form-label">{{
							translate("selectLbl")
						}}</label>

						<section class="my_radios">
							<div class="form-check">
								<input
									class="form-check-input"
									v-model="form.type"
									type="radio"
									value="1"
								/>
								<label class="form-check-label" for="flexRadioDefault1">
									{{ translate("formNewInterventionType1") }}
								</label>
							</div>
							<div class="form-check">
								<input
									class="form-check-input"
									v-model="form.type"
									type="radio"
									value="2"
								/>
								<label class="form-check-label" for="flexRadioDefault2">
									{{ translate("formNewInterventionType2") }}
								</label>
							</div>
							<div class="form-check">
								<input
									class="form-check-input"
									v-model="form.type"
									type="radio"
									value="3"
								/>
								<label class="form-check-label" for="flexRadioDefault3">
									{{ translate("formNewInterventionType3") }}
								</label>
							</div>
						</section>

						<section class="form-check mt-3">
							<input
								class="form-check-input"
								type="checkbox"
								v-model="form.notifications"
								value=""
								id="subscribenews"
								checked
								style="text-align: left: important;"
							/>
							<label class="form-check-label" for="subscribenews">
								{{ translate("formNewInterventionNotifications") }}</label
							>
						</section>
					</section>
				</section>

