						@click="cleanForm"
						type="button"
						class="btn mt-4 me-4 my-button"
					>
						{{ translate("btnClean") }}
					</button>
					<button @click="back" type="button" class="btn mt-4 my-button">
						{{ translate("btnBack") }}
					</button>

					<button class="btn btn-primary" type="button" disabled>
						<span
							class="spinner-border spinner-border-sm"
							role="status"
							aria-hidden="true"
						></span>
