/*
MEIW - Programação Web Avançada - projeto final
Auhtor: Duarte Cota
Description: implementation of the view Ficha de Inscrição
*/

<template>
  <section class="scrolling-component" ref="scrollcomponent" name="lang">
    <section class="container my-body">
      <h1 class="text-center mt-5">{{translate("newAccFormTitle")}}</h1>
      <section class="row align-middle" heigth="100">
        <section class="col-md-4 g-4">
          <label for="nif" class="form-label">{{translate('profilePhoto')}}</label>
          <label for="files" class="btn">Select Image</label>
            <input id="files" type="file" accept="image/*" @change="onChange" />
        </section>
        <section class="col-md-8 g-4">
          <div id="preview">
              <img v-if="item.imageUrl" :src="item.imageUrl" width="100" height="100" class="rounded-circle me-2" />
          </div>
        </section>
      </section>
      <section v-if=isShow
        class="alert mt-3"
        role="alert"
        v-bind:class="'alert-' + message.type"
      >
        {{ message.msg }}
      </section>
      <form class="form-signin" v-on:submit.prevent="send">
        <section class="row mt-4">
          <section class="col-md-2 g-4">
            <label for="firstname" class="form-label">{{translate('newAccFnamePh')}}</label>
            <input
              type="text"
              v-model="form.firstname"
              class="form-control"
              id="firstname"
              style="font-size: small"
            />
          </section>
          <section class="col-md-2 g-4">
            <label for="lastname" class="form-label">{{translate('newAccLnamePh')}}</label>
            <input
              type="text"
              v-model="form.lastname"
              class="form-control"
              id="lastname"
              style="font-size: small"
            />
          </section>
          <section class="col-md-8 g-4">
            <label for="lastname" class="form-label">{{translate('newAccNamePh')}}</label>
            <input
              type="text"
              v-model="form.name"
              class="form-control"
              id="name"
              placeholder="nome completo..."
              style="font-size: small"
            />
          </section>
        </section>
        <section class="row">
          <section class="col-md-4 g-4">
            <label for="type" class="form-label">{{translate('selectLbl')}}</label>
            <select class="form-select" 
              aria-label="Default select example"
              style="font-size: small">
              <option value="1">{{translate('typeOp1')}}</option>
              <option value="2">{{translate('typeOp2')}}</option>
              <option value="3">{{translate('typeOp3')}}</option>
            </select>
          </section>
          <section class="col-md-4 g-4">
            <label for="nif" class="form-label">{{translate('newAccNifPh')}}</label>
            <input
              type="text"
              v-model="form.nif"
              class="form-control"
              id="course"
              style="font-size: small"
            />
          </section>
          <section class="col-md-4 g-4">
            <label for="nif" class="form-label">{{translate('newAccEmailPh')}}</label>
            <input
              type="text"
              v-model="form.email"
              class="form-control"
              id="email"
              style="font-size: small"
            />
          </section>
        </section>
        <section class="row">
          <section class="col-md-6 g-4">
            <label for="nif" class="form-label">{{translate('newAccMobilePh')}}</label>
            <input
              type="text"
              v-model="form.mobile"
              class="form-control"
              id="mobile"
              style="font-size: small"
            />
          </section>
          <section class="col-md-6 g-4">
            <label for="nif" class="form-label">{{translate('newAccBdatePh')}}</label>
            <input
              class="form-control textbox-n"
              type="text"
              v-model="form.bdate"
              onfocus="(this.type = 'date')"
              id="bdate"
              style="font-size: small"
            />
          </section>
        </section>
        <section class="row">
          <section class="col-md-6 g-4">
            <label for="nif" class="form-label">{{translate('newAccUserPh')}}</label>
            <input
              type="text"
              v-model="form.auth.username"
              class="form-control"
              id="username"
              style="font-size: small"
            />
          </section>
          <section class="col-md-6 g-4">
            <label for="nif" class="form-label">{{translate('newAccPassPh')}}</label>
            <input
              type="password"
              v-model="form.auth.password"
              class="form-control"
              id="password"
              style="font-size: small"
            />
          </section>
        </section>
        <section class="row mt-4">
          <section>
            <section class="form-check">
              <input
                class="form-check-input"
                type="checkbox"
                v-model="form.notifications"
                value=""
                id="subscribenews"
                checked
                style="text-align: left: important;"
              />
              <label class="form-check-label" for="subscribenews">
                {{translate('newAccSubs')}}</label
              >
            </section>
          </section>
        </section>
        <section class="text-center">
          <button
            type="submit"
            class="btn mt-4 me-4 my-button"
          >
          {{translate('btnSubmit')}}
          </button>
          <button
            @click="cleanForm()"
            type="button"
            class="btn mt-4 me-4 my-button"
          >
          {{translate('btnClean')}}
          </button>
          <button
            @click="back()"
            type="button"
            class="btn mt-4 my-button"
          >
          {{translate('btnBack')}}
          </button>
        </section>
      </form>
      <section class="text-center">
        <section v-if=!isShow class="text-center">
          <div class="spinner-border mt-4" role="status">
            <span class="visually-hidden">Loading...</span>
          </div>
          <section class="mb-2"> {{translate('spinnerTxt')}}</section>
        </section>
      </section>
      <section class="spacer"></section>
    </section>
  </section>
</template>

<style scoped>
.my-button {
  width: 120px;
  background-color: black;
  color:white;
}

.my-button:hover {
  background-color: white;
  color:black;
}
select option[disabled]:first-child {
  display: none;
}
.my-body {
  margin-top: 0px;
}
.spacer {
  height: 200px;
}
.top-btn {
  font-weight: bold;
  width: 100px;
}
</style>

<script>
import en from '../assets/en.js'
import pt from '../assets/pt.js'
import axios from "axios";
import { mapMutations } from "vuex";
import { LOADING_SPINNER_SHOW_MUTATION } from "../store/storeconstants";
export default {
  name: "submit",
  mixins: [en,pt],
  data() {
    const lang = localStorage.getItem('lang')||'en';
    return {
      form: {
        firstname: "",
        lastname: "",
        name: "",
        nif: "",
        type: "",
        email: "",
        mobile: "",
        bdate: "",
        auth: {
          username: "",
          password: "",
        },
        notifications: true,
      },
      message: {
        type: "",
        msg: "",
      },
      isShow:false,
      lang: lang,
      item:{
          image : null,
          imageUrl: null
      }
    };
  },
  methods: {
    ...mapMutations({
      showLoader: LOADING_SPINNER_SHOW_MUTATION,
    }),
    async send() {
      (this.message.type = ""), (this.message.msg = "");
      let postData = {
        firstname: this.form.firstname,
        lastname: this.form.lastname,
        name: this.form.name,
        course: this.form.course,
        class: this.form.class,
        email: this.form.email,
        mobile: parseInt(this.form.mobile),
        accepted: false,
        level: "student",
        bdate: this.form.bdate,
        auth: {
          username: this.form.auth.username,
          password: this.form.auth.password,
        },
        notifications: this.form.notifications,
      };
      if (this.checkForm() == true) {
        this.showLoader(true);
        await axios
          //.post("https://cprob-api.herokuapp.com/user", postData)
          .post("http://localhost:3000/user", postData)
          .then((response) => {
            if (response.data.http == 201) {
              this.showLoader(false);
              this.message.type = "success";
              this.message.msg = "Utilizador criado com sucesso. A sua inscrição fica pendente de aprovação.";
              this.isShow=true
            } else if (response.data.http == 200) {
              this.showLoader(false);
              this.message.type = "warning";
              this.message.msg = "Utilizador existente.";
              this.isShow=true
            } else {
              this.showLoader(false);
              this.message.type = "danger";
              this.message.msg = "Ocorreu um problema, tente de novo...";
              this.isShow=true
            }
          })
          .catch(() => {
            this.error = "Valores inválidos!";
            this.showLoader(false);
          });
      } else {
        this.message.type = "danger";
        this.message.msg = "Todos os campos deve estar preenchidos!";
        this.isShow=true
      }
    },
    cleanForm() {
      (this.form.firstname = ""),
        (this.form.lastname = ""),
        (this.form.name = ""),
        (this.form.course = ""),
        (this.form.class = ""),
        (this.form.email = ""),
        (this.form.mobile = ""),
        (this.form.bdate = ""),
        (this.form.auth = {
          username: "",
          password: "",
        }),
        (this.form.notifications = true)
        this.isShow=false
    },
    back() {
      this.$router.replace("/");
    },
    checkForm() {
      if (
        this.form.firstname != "" &&
        this.form.lastname != "" &&
        this.form.name != "" &&
        this.form.course != "" &&
        this.form.class != "" &&
        this.form.email != "" &&
        this.form.mobile != "" &&
        this.form.bdate != "" &&
        this.form.auth.username != "" &&
        this.form.auth.password != ""
      )
        return true;
      else return false;
    },
    translate(prop) {
      return this[this.lang][prop]
    },
    onChange(e) {
      const file = e.target.files[0]
      this.image = file
      this.item.imageUrl = URL.createObjectURL(file)
    }
  },
};
</script>
