            </section>
            <section class="text-center my-text-yellow" v-if="nHarvest > 0">
              <hr />
              <p>
                <i
                  class="fa-solid fa-circle-exclamation"
                  style="font-size: 30px"
                ></i>
              </p>
              {{ harvestText }}
              <hr />
              <section class="text-center">
                <button
                  @click="interventionsEvent"
                  type="button"
                  class="btn btn-warning"
                  style="font-size: small; width: 160px; color: white"
                >
                  {{ translate("dashBtnHarvest") }}
                </button>
              </section>
            </section>
          </section>
        </section>
      </section>
    </section>

    <!--2nd row-->
    <section class="row mt-3">
      <!--logs-->
      <section class="col-12 col-md-6 col-lg-6">
        <section
          class="card mb-3 mh-100 text-center"
          style="
            border-radius: 10px;
            box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.1),
              0 6px 20px 0 rgba(0, 0, 0, 0.1);
          "
        >
          <section class="card-body">
            <h6 class="card-title text-center">
              <strong
                ><span> {{ translate("dashLogsTitle") }}</span></strong
              >
            </h6>
            <section class="text-center my-text-green">
              <hr />
              <section v-if="!isShow">
                <p>asd</p>
                <p>asd</p>
              </section>
              <section
                v-else
                class="spinner-border spinner-border-sm"
                role="status"
              ></section>
              <hr />
              <section class="text-center" v-if="!isShow">
                <button
                  @click="this.$router.push('sooninterventions')"
                  type="button"
                  class="btn btn-success"
                  style="font-size: small; width: 160px"
                >
                  {{ translate("dashLogsBtn") }}
                </button>
              </section>
              <section
                v-else
                class="spinner-border spinner-border-sm"
                role="status"
              ></section>
            </section>
          </section>
        </section>
      </section>
      <section class="row">
        <!--apiaries-->
        <section class="col-12 col-md-3 col-lg-3">
          <section
            class="card mb-3 mh-100 text-center"
            style="
              height: 210px;
              border-radius: 10px;
              box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.1),
                0 6px 20px 0 rgba(0, 0, 0, 0.1);
            "
          >
            <section class="card-body">
              <h6 class="card-title text-center">
                <strong
                  ><span> {{ translate("dashTotalApiaries") }}</span></strong
                >
              </h6>
              <hr />
              <section class="text-center my-indicator-green">
                <section v-if="!isShow">{{ totalApiaries }}</section>
                <section
                  v-else
                  class="spinner-border spinner-border-sm"
                  role="status"
                ></section>
                <hr />
                <section class="text-center">
                  <button
                    @click="this.$router.push('newapiary')"
                    type="button"
                    class="btn btn-success"
                    style="font-size: small; width: 160px"
                  >
                    {{ translate("dashApiariesBtn") }}
                  </button>
                </section>
              </section>
            </section>
          </section>
        </section>
        <!--hives-->
        <section class="col-12 col-md-3 col-lg-3">
          <section
            class="card mb-3 mh-100 text-center"
            style="
              height: 210px;
              border-radius: 10px;
              box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.1),
                0 6px 20px 0 rgba(0, 0, 0, 0.1);
            "
          >
            <section class="card-body">
              <h6 class="card-title text-center">
                <strong
                  ><span> {{ translate("dashTotalHives") }}</span></strong
                >
              </h6>
              <hr />
              <section class="text-center my-indicator-green">
                <section v-if="!isShow">{{ totalHives }}</section>
                <section
                  v-else
                  class="spinner-border spinner-border-sm"
                  role="status"
                ></section>
                <hr />
                <section class="text-center">
                  <button
                    @click="this.$router.push('newdevice')"
                    type="button"
                    class="btn btn-success"
                    style="font-size: small; width: 160px"
                  >
                    {{ translate("dashHivesBtn") }}
                  </button>
                </section>
              </section>
            </section>
          </section>
        </section>
      </section>
      <section class="row">
        <!--apiaries-->
        <section class="col-12 col-md-3 col-lg-3">
          <section
            class="card mb-3 mh-100 text-center"
            style="
              height: 210px;
              border-radius: 10px;
              box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.1),
                0 6px 20px 0 rgba(0, 0, 0, 0.1);
            "
          >
            <section class="card-body">
              <h6 class="card-title text-center">
                <strong
                  ><span> {{ translate("dashTotalApiaries") }}</span></strong
                >
              </h6>
              <hr />
              <section class="text-center my-indicator-green">
                <section v-if="!isShow">{{ totalApiaries }}</section>
                <section
                  v-else
                  class="spinner-border spinner-border-sm"
                  role="status"
                ></section>
                <hr />
                <section class="text-center">
                  <button
                    @click="this.$router.push('newapiary')"
                    type="button"
                    class="btn btn-success"
                    style="font-size: small; width: 160px"
                  >
                    {{ translate("dashApiariesBtn") }}
                  </button>
                </section>
              </section>
            </section>
          </section>
        </section>
        <!--hives-->
        <section class="col-12 col-md-3 col-lg-3">
          <section
            class="card mb-3 mh-100 text-center"
            style="
              height: 210px;
              border-radius: 10px;
              box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.1),
                0 6px 20px 0 rgba(0, 0, 0, 0.1);
            "
          >
            <section class="card-body">
              <h6 class="card-title text-center">
                <strong
                  ><span> {{ translate("dashTotalHives") }}</span></strong
                >
              </h6>
              <hr />
              <section class="text-center my-indicator-green">
                <section v-if="!isShow">{{ totalHives }}</section>
                <section
                  v-else
                  class="spinner-border spinner-border-sm"
                  role="status"
                ></section>
                <hr />
                <section class="text-center">
                  <button
                    @click="this.$router.push('newdevice')"
                    type="button"
                    class="btn btn-success"
                    style="font-size: small; width: 160px"
                  >
                    {{ translate("dashHivesBtn") }}
                  </button>
                </section>
              </section>
            </section>
          </section>
        </section>
      </section>
    </section>
    <!--3rd row-->
    <section class="row mt-3"></section>
  </section>
</template>

<style scoped>
