/*
MEIW - Programação Web Avançada - projeto final
Auhtor: Duarte Cota
Description: implementation of routes and related constrains
*/

import { createRouter, createWebHistory } from 'vue-router'
/*import Home from '../views/Home.vue'
import Login from '../views/Login.vue'
import Enrollment from '../views/Enrollment.vue'
import Admin from '../views/Admin.vue'
import Notif from '../views/Notif.vue'
import News from '../views/News.vue'
import Conta from '../views/Account.vue'*/
import store from '../store/store'
import Dashboard from '../views/Dashboard'
import { IS_USER_AUTHENTICATED_GETTER } from '../store/storeconstants'
import { GET_USER_ID_GETTER } from '../store/modules/auth/getters'

const routes = [{
    path: '',
    component: Dashboard,
    children: [{
            path: '',
            component: () =>
                import ('../views/Home.vue'),
            meta: { auth: false }
        },
        {
            path: 'login',
            component: () =>
                import ('../views/Login.vue'),
            meta: { auth: false }
        },
        {
            path: 'newaccount',
            component: () =>
                import ('../views/Enrollment.vue'),
            meta: { auth: false }
        },
        {
            path: 'account',
            component: () =>
                import ('../views/Account.vue'),
            meta: { auth: true }
        },
    ]
}]

const router = createRouter({
    history: createWebHistory(process.env.BASE_URL),
    routes
})

router.beforeEach((to, from, next) => {
    console.log(store.getters[`auth/${IS_USER_AUTHENTICATED_GETTER}`])
    if (to.meta.auth && !store.getters[`auth/${IS_USER_AUTHENTICATED_GETTER}`]) {
        next('/Login')
    } else {
        next(from.GET_USER_ID_GETTER)
    }
})

export default router
