/*
MEIW - Programação Web Avançada - projeto final
Auhtor: Duarte Cota
Description: implementation of the view Ficha de Inscrição
*/

<template>
  <section class="scrolling-component" ref="scrollcomponent" name="lang">
    <section class="container my-body">
      <h1 class="text-center mt-5">{{ translate("newAccFormTitle") }}</h1>
      <section
        v-if="showsection"
        class="alert mt-3 alert-dismissible fade show"
        role="alert"
        v-bind:class="'alert-' + message.type"
      >
        {{ message.msg }}
      </section>
      <form class="form-signin" v-on:submit.prevent="send">
        <section class="row">
          <section class="col-md-6 g-4">
            <label for="mobile" class="form-label">{{
              translate("formNewApiaryLocation")
            }}</label>
            <input
              type="text"
              v-model="form.mobile"
              class="form-control"
              id="mobile"
              style="font-size: small"
            />
          </section>
          <section class="col-md-6 g-4">
            <label for="bdate" class="form-label">{{
              translate("formNewApiaryAddress")
            }}</label>
            <input
              class="form-control textbox-n"
              type="text"
              v-model="form.bdate"
              onfocus="(this.type = 'date')"
              id="bdate"
              style="font-size: small"
            />
          </section>
        </section>
        <section class="row">
          <section class="col-md-12 g-4">
            <label for="username" class="form-label">{{
              translate("formNewApiaryObservations")
            }}</label>
            <textarea
              rows="5"
              v-model="form.auth.username"
              class="form-control"
              id="username"
              style="font-size: small"
            />
          </section>
        </section>

        <section class="text-center">
          <button @click="send()" type="submit" class="btn mt-4 me-4 my-button">
            {{ translate("btnSubmit") }}
          </button>
          <button
            @click="cleanForm()"
            type="button"
            class="btn mt-4 me-4 my-button"
          >
            {{ translate("btnClean") }}
          </button>
          <button @click="back()" type="button" class="btn mt-4 my-button">
            {{ translate("btnBack") }}
          </button>
        </section>
      </form>
      <section class="text-center">
        <section v-if="isShow" class="text-center">
          <section class="spinner-border mt-4" role="status">
            <span class="visually-hidden">Loading...</span>
          </section>
          <section class="mb-2">{{ translate("spinnerTxt") }}</section>
        </section>
      </section>
      <section class="spacer"></section>
    </section>
  </section>
</template>

<style scoped>
.my-button {
  width: 120px;
  background-color: black;
  color: white;
}

.my-button:hover {
  background-color: white;
  color: black;
}
select option[disabled]:first-child {
  display: none;
}
.my-body {
  margin-top: 0px;
}
.spacer {
  height: 200px;
}
.top-btn {
  font-weight: bold;
  width: 100px;
}
</style>

<script>
  import en from "../assets/en.js";
  import pt from "../assets/pt.js";
  import axios from "axios";
  import { mapMutations } from "vuex";
  import { LOADING_SPINNER_SHOW_MUTATION } from "../store/storeconstants";
  export default {
    name: "submit",
    mixins: [en, pt],
    data() {
      const lang = localStorage.getItem("lang") || "en";
      return {
        form: {
          firstname: "",
          lastname: "",
          name: "",
          nif: "",
          type: "",
          email: "",
          mobile: "",
          bdate: "",
          auth: {
            username: "",
            password: "",
          },
          notifications: true,
        },
        message: {
          type: "",
          msg: "",
        },
        isShow: false,
        showsection: false,
        lang: lang,
        item: {
          image: null,
          imageUrl: null,
        },
        avatar: require("../assets/avatar.png"),
      };
    },
    methods: {
      ...mapMutations({
        showLoader: LOADING_SPINNER_SHOW_MUTATION,
      }),
      async send() {
        (this.message.type = ""), (this.message.msg = "");
        let postData = new FormData();
        postData.append("firstname", this.form.firstname);
        postData.append("lastname", this.form.lastname);
        postData.append("name", this.form.name);
        postData.append("email", this.form.email);
        postData.append("nif", this.form.nif);
        postData.append("mobile", parseInt(this.form.mobile));
        postData.append("level", "beekeeper");
        postData.append("type", this.form.type);
        postData.append("bdate", this.form.bdate);
        postData.append("username", this.form.auth.username);
        postData.append("password", this.form.auth.password);
        postData.append("notifications", this.form.notifications);
        postData.append("img", this.item.image);
        postData.append("lang", this.lang);
        if (this.checkForm() == true) {
          this.isShow = true;
          await axios
            .post("https://bhsapi.duartecota.com/user", postData, {
              headers: { "Content-Type": "multipart/form-data" },
            })
            .then((response) => {
              console.log(response.data.http);
              if (response.data.http == 201) {
                this.isShow = false;
                this.showsection = true;
                this.message.type = "success";
                this.message.msg = this.translate("mesNewAccount");
              } else if (response.data.http == 200) {
                this.isShow = false;
                this.showsection = true;
                this.message.type = "warning";
                this.message.msg = this.translate("mesUserExists");
              } else {
                this.isShow = false;
                this.showsection = true;
                this.message.type = "danger";
                this.message.msg = this.translate("mesProblem");
              }
            })
            .catch((error) => {
              console.log(error);
              this.isShow = false;
              this.showsection = true;
              this.message.type = "danger";
              this.message.msg = this.translate("mesProblem");
            });
        } else {
          this.isShow = false;
          this.showsection = true;
          this.message.type = "danger";
          this.message.msg = this.translate("mesFields");
        }
      },
      cleanForm() {
        this.message.type = "";
        this.message.msg = "";
        (this.form.firstname = ""),
          (this.form.lastname = ""),
          (this.form.name = ""),
          (this.form.type = ""),
          (this.form.nif = ""),
          (this.form.email = ""),
          (this.form.mobile = ""),
          (this.form.bdate = ""),
          (this.form.auth = {
            username: "",
            password: "",
          }),
          (this.form.notifications = true);
        this.item.imageUrl = null;
        this.item.image = null;
        this.isShow = false;
        this.showsection = false;
      },
      back() {
        this.$router.replace("/");
      },
      checkForm() {
        if (
          this.form.firstname == "" ||
          this.form.lastname == "" ||
          this.form.name == "" ||
          this.form.type == "" ||
          this.form.nif == "" ||
          this.form.email == "" ||
          this.form.mobile == "" ||
          this.form.bdate == "" ||
          this.form.auth.username == "" ||
          this.form.auth.password == "" ||
          this.form.image == ""
        )
          return false;
        else return true;
      },
      translate(prop) {
        return this[this.lang][prop];
      },
      onChange(e) {
        const file = e.target.files[0];
        this.item.image = file; //o que deve ser enviado
        this.item.imageUrl = URL.createObjectURL(file);
      },
    },
  };
</script>
