/*
MEIW - Programação Web Avançada - projeto final
Auhtor: Duarte Cota
Description: implementation of the view Gestão de Alunos (Admin)
*/

<template id="example-modal">
  <section class="container">
    <section class="row mt-3 text-center">
      <h1 class="text-center">{{ translate("usersTitle") }}</h1>
    </section>
    <section
      v-if="showsection"
      class="alert mt-3"
      role="alert"
      v-bind:class="'alert-' + message.type"
    >
      {{ message.msg }}
    </section>
    <section class="row mt-3">
      <h4>{{ translate("usersLbl") }}</h4>
    </section>
    <section class="card mt-2">
      <section class="card-body">
        <table class="table mt-2">
          <tbody>
            <tr>
              <th>{{ translate("thdName") }}</th>
              <th>{{ translate("thdStatus") }}</th>
              <th>{{ translate("thdLastAccess") }}</th>
              <th class="text-center">{{ translate("thdActions") }}</th>
            </tr>
            <tr v-for="user of this.users" :key="user._id">
              <td>{{ user.name }}</td>
              <td>{{ user.status }}</td>
              <td>{{ user.lastlogin }}</td>
              <td class="text-center">
                <button
                  @click="detailsModal(user._id)"
                  type="button"
                  class="btn btn-success btn-sm me-2 ac-btn"
                >
                  <i class="fas fa-search me-1" aria-hidden="true"></i>
                  {{ translate("btnDetails") }}
                </button>
                <button
                  @click="cancel(user._id)"
                  type="button"
                  class="btn btn-danger btn-sm me-2 ac-btn"
                >
                  <i class="far fa-trash-alt me-1" aria-hidden="true"></i
                  >Remover
                </button>
              </td>
              <Modal v-show="isModalVisible" @close="closeModal" />
            </tr>
          </tbody>
        </table>
      </section>
    </section>

    <section class="text-center">
      <section v-if="isShow" class="text-center">
        <section class="spinner-border mt-4" role="status">
          <span class="visually-hidden">Loading...</span>
        </section>
        <section class="mb-2">{{ translate("spinnerTxt") }}</section>
      </section>
    </section>
    <section class="spacer"></section>
  </section>
</template>

<style scoped>
.ac-btn {
  width: 120px;
  color: white;
  font-weight: bold;
}
.spacer {
  height: 200px;
}
</style>

<script>
  import en from "../assets/en.js";
  import pt from "../assets/pt.js";
  import axios from "axios";
  import ModalUserDetails from "../components/ModalUserDetails.vue";
  //import { LOADING_SPINNER_SHOW_MUTATION } from "../store/storeconstants";
  export default {
    mixins: [en, pt],
    components: {
      ModalUserDetails,
    },
    data() {
      const lang = localStorage.getItem("lang") || "pt";
      return {
        data: localStorage.token,
        users: [],
        showModal: true,
        form: {
          firstname: "",
          lastname: "",
          name: "",
          mobile: "",
          type: "",
          email: "",
          mobile: "",
          bdate: "",
          notifications: false,
        },
        message: {
          type: "",
          msg: "",
        },
        state: true,
        showsection: false,
        isShow: false,
        lang: lang,
        isModalVisible: false,
      };
    },
    mounted() {
      this.getUsers();
    },
    methods: {
      translate(prop) {
        return this[this.lang][prop];
      },
      async getUsers() {
        this.users = [];
        this.isShow = true;
        await axios
          .get("https://bhsapi.duartecota.com/user", {
            headers: {
              Authorization: this.token,
            },
          })
          .then((response) => {
            this.isShow = false;
            let users = response.data.body;
            for (let i = 0; i < users.length; i++) {
              if (users[i].level == "beekeeper") {
                this.users.push({
                  _id: users[i]._id,
                  name: users[i].name,
                  status:
                    users[i].active == true
                      ? this.translate("usersActive")
                      : this.translate("usersInactive"),
                  lastlogin:
                    users[i].lastlogin == null
                      ? this.translate("lastLoginNever")
                      : users[i].lastlogin,
                });
              }
            }
          })
          .catch((error) => {
            this.showLoader(false);
            alert(error);
          });
      },
      /*async acceptStd(_id) {
                                                                                                                                  this.showLoader(true);
                                                                                                                                  await axios
                                                                                                                                    .put("https://cprob-api.herokuapp.com/user/" + _id, {
                                                                                                                                      headers: {
                                                                                                                                        Authorization: this.token,
                                                                                                                                      },
                                                                                                                                    })
                                                                                                                                    .then(() => {
                                                                                                                                      this.message.msg = "Utilizador aceite!";
                                                                                                                                      this.message.type = "success";
                                                                                                                                      this.isShow = true;
                                                                                                                                      this.showLoader(false);
                                                                                                                                      this.getUsers();
                                                                                                                                    })
                                                                                                                                    .catch((error) => {
                                                                                                                                      console.log(error);
                                                                                                                                    });
                                                                                                                                },*/
      async cancel(id) {
        await axios
          .put("https://bhsapi.duartecota.com/user/" + id, {
            headers: {
              Authorization: this.token,
            },
          })
          .then((response) => {
            if (response.data.http == 200) {
              this.isShow = false;
              this.showsection = true;
              this.message.type = "success";
              this.message.msg = this.translate("cancelAccMessage");
              setTimeout(() => (this.showsection = false), 3000);
              this.getUsers();
            } else {
              this.isShow = false;
              this.showsection = true;
              this.message.type = "danger";
              this.message.msg = this.translate("mesProblem");
            }
          })
          .catch(() => {
            this.isShow = false;
            this.showsection = true;
            this.error = this.translate("mesProblem");
          });
      },
      async detail(_id) {
        (this.isShow = true((this.message.type = ""))), (this.message.msg = "");
        await axios
          .get("https://bhsapi.duartecota.com/user/" + _id, {
            headers: {
              Authorization: this.token,
            },
          })
          .then((response) => {
            (this.form.firstname = response.data.body.firstname),
              (this.form.lastname = response.data.body.lastname),
              (this.form.name = response.data.body.name),
              (this.form.type = response.data.body.type),
              (this.form.email = response.data.body.email),
              (this.form.bdate = response.data.body.bdate),
              (this.form.mobile = response.data.body.mobile);
            if (response.data.body.notifications == true)
              this.form.notifications = "SIM";
            else this.form.notifications = "NÃO";
            //this.state = true;
          })
          .catch(() => {
            this.message.msg = "Ocorreu um problema";
            this.message.type = "warning";
            this.isShow = false;
          });
      },
      detailsModal(id) {
        this.isModalVisible = true;
      },
      closeModal(id) {
        this.isModalVisible = false;
      },
    },
  };
</script>
