/*
MEIW - Programação Web Avançada - projeto final
Auhtor: Duarte Cota
Description: implementation of the view Home
*/

<template>
  <section class="jumbotron d-flex align-items-center min-vh-100">
    <section class="text-center">
      <img class="my-img" :src="logo" alt="image" />
    </section>
  </section>
</template>

<style scoped>
.my-body {
  display: flex;
  align-items: center;
}

.my-img {
  max-width: 30%;
  height: auto;
}

h3 {
  color: #c55a11 !important;
  position: relative;
  margin-top: 40px;
  font-family: "Times New Roman", Times, serif;
  letter-spacing: 10px;
}
</style>     

<script>
  import axios from "axios";
  import { mapActions, mapGetters } from "vuex";
  import {
    IS_USER_AUTHENTICATED_GETTER,
    GET_USER_ID_GETTER,
    GET_USER_TOKEN_GETTER,
    AUTO_NUMEVENTS_ACTION,
  } from "../store/storeconstants";
  export default {
    data: function () {
      const lang = localStorage.getItem("lang") || "pt";
      return {
        logo: require("../assets/IMG1224.png"),
        lang: lang,
      };
    },
    computed: {
      ...mapGetters("auth", {
        isAuthenticated: IS_USER_AUTHENTICATED_GETTER,
        token: GET_USER_TOKEN_GETTER,
        _id: GET_USER_ID_GETTER,
        auth: localStorage.getItem("auth"),
      }),
    },
    mounted() {
      this.getNumEvents();
    },
    methods: {
      ...mapActions("auth", {
        _numEventsChng: AUTO_NUMEVENTS_ACTION,
      }),
    },
    async numEventsChng(num) {
      await this._numEventsChng(num);
    },
    async getNumEvents() {
      await axios
        .get("https://bhsapi.duartecota.com/event/num/" + this._id, {
          headers: {
            Authorization: this.token,
          },
        })
        .then((response) => {
          if (response.data.body.length == 0) {
            this.hasEvents = false;
          } else {
            this.hasEvents = true;
            console.log(response.data.body);
            this.numEventsChng({
              numEvents: response.data.body,
            });
          }
        })
        .catch((error) => {
          notify({
            title: this.translate("notifErrorTitle"),
            text: this.translate("mesProblem"),
            type: "error",
            duration: 3000,
            speed: 500,
          });
        });
    },
  };
</script>
