        <p>© Copyright 2022 BHiveSense - todos os direitos reservados</p>
      </section>
    </section>
  </section>
</template>

<style scoped>
.footer a {
  color: white;
}
.footer a:hover {
  opacity: 0.5;
}
.footer {
  position: fixed;
  display: flex;
  width: 100%;
  flex-direction: column;
  align-items: center;
  padding: 0.2em;
  bottom: 0px;
  background-color: #2b589f;
  overflow: visible;
}
.footer li {
  display: inline;
  padding-right: 0.5em;
  font-size: 2em;
}
ul.social-media {
  padding: 0;
}
@media (min-width: 768px) {
  .footer {
    flex-direction: row;
    justify-content: center;
  }
  .footer li {
    font-size: 2em;
  }
}
</style>
