  onMounted(() => {
    observer = new (window as any).ResizeObserver((entries: any) => {
      const w = entries[0].contentRect.width
      const h = entries[0].contentRect.height

      onUpdate(w, h)
    })

    observer.observe(el.value)
  })

  onUnmounted(() => {
    observer.disconnect()
  })

  return { width, height, onResize }
