    watch(mouse, () => {
      if (chart.bandScale) {
        const vals = chart.getData(chart.getKeys(0)) as number[]
        const [x] = chart.bandScale.map([vals[mouse.index]])
        const width = chart.bandScale.bandwidth()

        if (x) {
          if (chart.config.direction === 'horizontal') {
            bar.value = {
              x: x - width / 2,
